// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

import "./FugaziStorageLayout.sol";

// This facet will handle pool registry operations
contract FugaziPoolRegistryFacet is FugaziStorageLayout {
    // create pool
    function createPool(address tokenX, address tokenY, inEuint32 calldata _initialReserves) external {
        // transform the type
        euint32 initialReserves = FHE.asEuint32(_initialReserves);

        // adjust the input; we cannot create a pool with reserves more than the owner has
        euint32 availabeX = FHE.min(account[msg.sender].balanceOf[tokenX], FHE.shr(initialReserves, FHE.asEuint32(16)));
        euint32 availabeY =
            FHE.min(account[msg.sender].balanceOf[tokenY], FHE.and(initialReserves, FHE.asEuint32(2 ^ 16 - 1)));

        // minimum reserves: at least 2048 of each token
        FHE.req(FHE.and(FHE.gt(availabeX, FHE.asEuint32(2048)), FHE.gt(availabeY, FHE.asEuint32(2048))));

        // deduct the token balance of msg.sender
        account[msg.sender].balanceOf[tokenX] = account[msg.sender].balanceOf[tokenX] - availabeX;
        account[msg.sender].balanceOf[tokenY] = account[msg.sender].balanceOf[tokenY] - availabeY;

        // construct input
        poolCreationInputStruct memory poolCreationInput = poolCreationInputStruct({
            tokenX: tokenX,
            tokenY: tokenY,
            initialReserveX: availabeX,
            initialReserveY: availabeY
        });

        // create pool
        _createPool(poolCreationInput);
    }

    function _createPool(poolCreationInputStruct memory i) internal {
        // check if pool already exists
        bytes32 poolId = getPoolId(i.tokenX, i.tokenY);
        if (poolId != bytes32(0)) {
            revert PoolAlreadyExists();
        }

        // update pool id mapping
        poolIdMapping[i.tokenX][i.tokenY] = i.tokenX < i.tokenY
            ? keccak256(abi.encodePacked(i.tokenX, i.tokenY))
            : keccak256(abi.encodePacked(i.tokenY, i.tokenX));

        // initialize pool
        poolStateStruct storage $ = poolState[poolId];
        _initializePool($, i);

        // emit event
        emit PoolCreated(i.tokenX, i.tokenY, poolId);
    }

    function _initializePool(poolStateStruct storage $, poolCreationInputStruct memory i) internal {
        // set token addresses
        $.tokenX = i.tokenX;
        $.tokenY = i.tokenY;

        // set epoch & settlement time
        $.epoch = 0;
        $.lastSettlement = uint32(block.timestamp);

        // take half of fee and set protocol account balances
        $.protocolX = FHE.shr(i.initialReserveX, FHE.asEuint32(feeBitShifts + 1));
        $.protocolY = FHE.shr(i.initialReserveY, FHE.asEuint32(feeBitShifts + 1));

        // mint LP token and take another half of fee
        $.lpTotalSupply = FHE.max($.batch[0].reserveX0, $.batch[0].reserveY0);
        $.lpBalanceOf[address(this)] = FHE.shr($.lpTotalSupply, FHE.asEuint32(feeBitShifts + 1)); // this will be locked permanently
        $.lpBalanceOf[msg.sender] = $.lpTotalSupply - $.lpBalanceOf[address(this)];
    }

    // get pool id
    function getPoolId(address tokenX, address tokenY) public view returns (bytes32) {
        return tokenX < tokenY ? poolIdMapping[tokenX][tokenY] : poolIdMapping[tokenY][tokenX];
    }
}

contracts/FugaziStorageLayout.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

import "@fhenixprotocol/contracts/FHE.sol";
import {Permissioned, Permission} from "@fhenixprotocol/contracts/access/Permissioned.sol";

// This will be used to store all the storage variables & will be inherited by all of the other contracts
// every library should be imported here
contract FugaziStorageLayout is Permissioned {
    // import FHE library
    using FHE for euint32;

    /*:.+.*.*:.*..:..*.*:.*..:.+.*.*:*/
    /*                           Diamond                          */
    /*.:.+.*.:*.*.+.:*.*..:.:.*.:*.+.*/

    // errors
    error noCorrespondingFacet();
    error notOwner();

    // events

    // modifiers
    modifier onlyOwner() {
        if (msg.sender != owner) revert notOwner();
        _;
    }

    // structs
    struct facetAndSelectorStruct {
        address facet;
        bytes4 selector;
    }

    // storage variables
    address internal owner;
    mapping(bytes4 => address) internal selectorTofacet;

    /*:.+.*.*:.*..:..*.*:.*..:.+.*.*:*/
    /*                       Account Facet                        */
    /*.:.+.*.:*.*.+.:*.*..:.:.*.:*.+.*/

    // errors

    // events - we do not emit amount to keep the confidentiality
    event Deposit(address recipient, address token);
    event Withdraw(address recipient, address token);

    // modifiers

    // structs
    struct unclaimedOrderStruct {
        bytes32 poolId;
        uint32 epoch;
    }

    struct accountStruct {
        mapping(address => euint32) balanceOf; // token address => balance
        unclaimedOrderStruct[] unclaimedOrders;
    }

    // storage variables
    mapping(address => accountStruct) internal account;

    /*:.+.*.*:.*..:..*.*:.*..:.+.*.*:*/
    /*                    Pool Registry Facet                     */
    /*.:.+.*.:*.*.+.:*.*..:.:.*.:*.+.*/

    // errors
    error PoolAlreadyExists();

    // events
    event PoolCreated(address tokenX, address tokenY, bytes32 poolId);

    // modifiers

    // structs
    struct poolCreationInputStruct {
        address tokenX;
        address tokenY;
        euint32 initialReserveX;
        euint32 initialReserveY;
    }

    struct poolStateStruct {
        // pool info
        address tokenX;
        address tokenY;
        uint32 epoch;
        uint32 lastSettlement;
        bool isSettling; // if true, any operation is not allowed. finish the settlement first
        // pool reserves
        euint32 reserveX;
        euint32 reserveY;
        // protocol account
        euint32 protocolX;
        euint32 protocolY;
        // LP token shares
        euint32 lpTotalSupply;
        mapping(address => euint32) lpBalanceOf;
        // information of each batch
        mapping(uint32 => batchStruct) batch; // batchStruct is defined in the Pool Action Facet section
    }

    // storage variables
    uint32 internal feeBitShifts = 10;
    mapping(address => mapping(address => bytes32)) internal poolIdMapping;
    mapping(bytes32 => poolStateStruct) internal poolState;

    /*:.+.*.*:.*..:..*.*:.*..:.+.*.*:*/
    /*                     Pool Action Facet                      */
    /*.:.+.*.:*.*.+.:*.*..:.:.*.:*.+.*/

    // errors
    error PoolNotFound();
    error EpochNotEnded();
    error OrderAlreadyClaimed();

    // events
    event epochSettled(bytes32 poolId, uint32 epoch);

    // modifiers
    modifier onlyValidPool(bytes32 poolId) {
        if (poolState[poolId].tokenX == address(0)) revert PoolNotFound();
        _;
    }

    // structs
    struct batchStruct {
        // mapping of each individual swap & mint order
        mapping(address => orderStruct) order;
        ////////////////////////////////////////////////
        //                                            //
        //      state right before the settlement     //
        //                                            //
        ////////////////////////////////////////////////
        // initial pool state
        euint32 reserveX0;
        euint32 reserveY0;
        // sum of swap orders
        euint32 swapX;
        euint32 swapY;
        // sum of mint orders
        euint32 mintX;
        euint32 mintY;
        ////////////////////////////////////////////////
        //                                            //
        //   intermidiate values used in settlement   //
        //                                            //
        ////////////////////////////////////////////////
        intermidiateValuesStruct intermidiateValues;
        ////////////////////////////////////////////////
        //                                            //
        //      state right after the settlement      //
        //                                            //
        ////////////////////////////////////////////////
        // final pool state
        euint32 reserveX1;
        euint32 reserveY1;
        // final output amounts
        euint32 outX;
        euint32 outY;
        // newly minted LP tokens
        euint32 lpIncrement;
    }

    struct orderStruct {
        euint32 swapX;
        euint32 swapY;
        euint32 mintX;
        euint32 mintY;
        bool claimed;
    }

    struct intermidiateValuesStruct {
        euint32 XForPricing;
        euint32 YForPricing;
    }

    // storage variables
    uint32 internal epochTime = 1 minutes;
}

contracts/FugaziViewerFacet.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.24;

import "./FugaziStorageLayout.sol";

// This facet contains all the viewer functions
// will be used for the easy access of the data from frontend
contract FugaziViewerFacet is FugaziStorageLayout {
    // get balance
    function getBalance(address token, Permission memory permission)
        external
        view
        onlySender(permission)
        returns (string memory)
    {
        // reencrypt and return
        return FHE.sealoutput(account[msg.sender].balanceOf[token], permission.publicKey);
    }
}

